	TEXT="子目录名称使用英文名称"/>
<node ID="Freemind_Link_1784051557" 
	TEXT="提交文档位于该目录下"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1298501033" 
	TEXT="docbook 文档">
<node ID="Freemind_Link_1225544067" 
	TEXT="首页文档名为  index.xml"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1778744887" 
